void.string:
-- EXPORT DATABASE SCHEMA/FILES --

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- User input
WbSysExec -program='python3' -argument='"$[pwb_path]/extract_user_input.py"' -env="PATH=$[py_path]";
-- Read user variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- create base directories
WbSysExec -ifEmpty=quit -program='python3' -argument='"$[pwb_path]/extract_sip_paths.py"' -env="PATH=$[py_path]";
-- Read user variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Check connection
WbConfirm -ifNotEmpty=db_args -message="Is database connection correct?" -yesText="Yes, run command" -noText="No, stop";
-- Create schemareport
WbInclude -ifNotEmpty=db_args -file=PWB/sqlwb/WbSchemaReport.sql -displayResult=true -verbose=true -continueOnError=false;
-- Generate WbCopy statements
WbInclude -ifNotEmpty=db_args -file=PWB/sqlwb/metadata2wbcopy.sql -displayResult=true -verbose=true -continueOnError=false;
-- Generate target connection
WbVarDef TargetCon='username=,password=,url=jdbc:h2:../_DATA/$[sys_name]/content/sub_systems/$[subsys_name]/documentation/$[db_name]_$[db_schema];LOG=0;CACHE_SIZE=65536;LOCK_MODE=0;UNDO_LOG=0';
-- Copy data to H2 database in data package
WbInclude -ifNotEmpty=db_args -file=tmp/wbcopy.sql -displayResult=true -verbose=true -continueOnError=false;
-- Create archives from directories
WbSysExec -ifNotEmpty=subsys_name -program='python3' -argument='"$[pwb_path]/extract_wim_capture.py"' -env="PATH=$[py_path]";

-- CREATE SYSTEM DATA PACKAGE -- 

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Create archive from extracted data
WbSysExec -program='python3' -argument='"$[pwb_path]/create_data_package.py"' -env="PATH=$[py_path]";

-- VERIFY CHECKSUM --

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- TODO: Finn raskere løsning
-- md5sum check
WbSysExec -program='python3' -argument='"$[pwb_path]/verify_md5sum.py"' -env="PATH=$[py_path]";

-- MAKE COPIES --

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- md5sum check
WbSysExec -program='python3' -argument='"$[pwb_path]/verify_make_copies.py"' -env="PATH=$[py_path]";

-- PROCESS FILES:

-- Fix directories and extract from H2 if exists
WbVarDef -file=tmp/pwb.ini; WbSysExec -program='python3' -argument='"$[pwb_path]/process_files_pre.py"' -env="PATH=$[py_path]";															
-- Virus check and metadata extract															
WbVarDef -file=tmp/pwb.ini; WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_files_check.py"' -env="PATH=$[py_path]";	
-- Convert files			
WbVarDef -file=tmp/pwb.ini; WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_files_convert.py"' -env="PATH=$[py_path]";	
-- Save log/unmount wim image																			
WbVarDef -file=tmp/pwb.ini; WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/wim_umount.py"' -env="PATH=$[py_path]";	
-- Clean up																	
WbVarDef -file=tmp/pwb.ini; WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/cleanup.py"' -env="PATH=$[py_path]";		
WbSysExec -ifEmpty=wim_path -program='echo "*** Some files not converted. Scroll up to see the list. ***"'

-- PROCESS METADATA:

-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;
-- Normalize metadata and generate sql	
WbSysExec -program='python3' -argument='"$[pwb_path]/process_metadata_pre.py"' -env="PATH=$[py_path]";
-- Read env variables from config-file
WbVarDef -file=tmp/pwb.ini;		
-- Test data import
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/process_metadata_check.py"' -env="PATH=$[py_path]";
-- Save log/unmount wim image																			
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/wim_umount.py"' -env="PATH=$[py_path]";	
-- Clean up																	
WbSysExec -ifNotEmpty=wim_path -program='python3' -argument='"$[pwb_path]/cleanup.py"' -env="PATH=$[py_path]";
